import { defineStyle, defineStyleConfig } from '@chakra-ui/react';

const baseStyle = {
  maxW: '1440px',
  margin: '0 auto',
  px: '18px',
};

const sizes = {
  sm: defineStyle({
    maxW: '45ch',
    p: '4',
  }),
  md: defineStyle({
    maxW: 'container.sm',
    p: '6',
    fontSize: 'lg',
  }),
  lg: defineStyle({
    maxW: '75ch',
    p: '8',
    fontSize: 'xl',
  }),
};

export const containerTheme = defineStyleConfig({ baseStyle, sizes });
