import { Heading, Input, Flex, Spacer } from '@chakra-ui/react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import Search from './Search';
import Gnb from './Gnb';

const Header = () => {
  return (
    <HeaderWrapper>
      <Heading as="h1" size="lg">
        <Link to="/">한국ST거래</Link>
      </Heading>
      <Gnb />
      <Spacer />
      <Flex alignItems={'center'}>
        <Search />
        <Langs>
          <Link className="on" to="/">
            KOR
          </Link>
          <Link to="/">ENG</Link>
        </Langs>
      </Flex>
    </HeaderWrapper>
  );
};

const Langs = styled(Flex)`
  margin-left: 0.6rem;
  a {
    display: flex;
    align-items: center;
    position: relative;
    height: 45px;
    padding: 0 1rem;
    white-space: nowrap;
    color: var(--gray-5);
    &::before {
      content: '';
      position: absolute;
      top: 50%;
      left: 0;
      transform: translateY(-45%);
      width: 1px;
      height: 12px;
      background: var(--gray-5);
    }
    &:first-child::before {
      display: none;
    }
    &:hover {
      color: var(--black);
    }
    &.on {
      color: var(--black);
    }
  }
`;

const Util = styled(Flex)`
  li {
    margin-left: 5px;
    a {
      display: flex;
      align-items: center;
      height: 45px;
      padding: 0 1.5rem;
      border-radius: 45px;
      background: var(--gray-2);
      white-space: nowrap;
      &:hover {
        color: #ccc;
      }
    }
  }
`;

const HeaderWrapper = styled.header`
  display: flex;
  height: var(--header-height);
  background: var(--primary);
  border-bottom: 1px solid var(--gray-2);
  padding: 0 3%;
  h1 a {
    display: flex;
    align-items: center;
    width: 154px;
    height: 100%;
    margin-right: 1rem;
    background: url('/images/comm/logo.svg') no-repeat 50% 50%;
    text-indent: -9999px;
  }
`;

export default Header;
