import { defineStyle, defineStyleConfig } from '@chakra-ui/styled-system';

const sizes = {
  xxl: defineStyle({
    maxW: '1440px',
    p: '8',
    fontSize: 'xl',
  }),
};

export const containerTheme = defineStyleConfig({ sizes });
