import { extendTheme, Container } from '@chakra-ui/react';
import { defineStyle, defineStyleConfig } from '@chakra-ui/styled-system';
import { createBreakpoints } from '@chakra-ui/theme-tools';

const sizes = {
  sm: defineStyle({
    maxW: '45ch',
    p: '4',
  }),
  md: defineStyle({
    maxW: 'container.sm',
    p: '6',
    fontSize: 'lg',
  }),
  lg: defineStyle({
    maxW: '75ch',
    p: '8',
    fontSize: 'xl',
  }),
};

export const containerTheme = defineStyleConfig({ sizes });

export const theme = extendTheme({
  Container: {
    baseStyle: {
      w: '100%',
      mx: 'auto',
      maxW: 'prose',
      px: '4',
    },
  },
});
