import { Heading, Input, Flex, Spacer } from '@chakra-ui/react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import Search from './Search';

const Header = () => {
  return (
    <HeaderWrapper>
      <Flex h={'80px'} p={'0 2%'}>
        <Heading as="h1" size="lg">
          logo
        </Heading>
        <Gnb>
          <ul>
            <li>
              <Link to="/Exchange">거래소</Link>
            </li>
            <li>
              <Link to="/Deposit">입출금</Link>
            </li>
            <li>
              <Link to="/Investment">투자내역</Link>
            </li>
            <li>
              <Link to="/Help">고객센터</Link>
            </li>
          </ul>
        </Gnb>
        <Spacer />
        <Flex alignItems={'center'}>
          <Search />
          <Flex className="util">
            <li>
              <Link to="/Login">로그인</Link>
            </li>
            <li>
              <Link to="/Signup">회원가입</Link>
            </li>
          </Flex>
          <Flex className="lang">
            <Link to="/">KOR</Link>
            <Link to="/">ENG</Link>
          </Flex>
        </Flex>
      </Flex>
    </HeaderWrapper>
  );
};

const Tnb = styled.nav`
  display: flex;
  margin-left: auto;
`;

const HeaderWrapper = styled.header`
  background: var(--white);
  border-bottom: 1px solid var(--gray-2);
  .inner {
    display: flex;
    align-items: center;
    padding: 0 5rem;
  }
  h1 {
    display: flex;
    align-items: center;
    height: 100%;
    margin-right: 1rem;
  }
`;

const Gnb = styled.nav`
  ul {
    display: flex;
    margin-left: auto;
    li {
      margin-left: 1rem;
      a {
        display: flex;
        align-items: center;
        height: 80px;
        padding: 0 1rem;
        &:hover {
          color: #ccc;
        }
      }
    }
  }
`;

export default Header;
