import { Heading, Input, Flex, Spacer } from '@chakra-ui/react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import Search from './Search';
import Gnb from './Gnb';

const Header = () => {
  return (
    <HeaderWrapper>
      <Flex h={'80px'} p={'0 2%'}>
        <Heading as="h1" size="lg">
          <Link to="/">한국ST거래</Link>
        </Heading>
        <Gnb />
        <Spacer />
        <Flex alignItems={'center'}>
          <Search />
          <Util>
            <li>
              <Link to="/Login">로그인</Link>
            </li>
            <li>
              <Link to="/Signup">회원가입</Link>
            </li>
          </Util>
          <Lang>
            <Link className="on" to="/">
              KOR
            </Link>
            <Link to="/">ENG</Link>
          </Lang>
        </Flex>
      </Flex>
    </HeaderWrapper>
  );
};

const Lang = styled(Flex)`
  margin-left: 0.6rem;
  a {
    display: flex;
    align-items: center;
    position: relative;
    height: 45px;
    padding: 0 1rem;
    white-space: nowrap;
    color: var(--gray-5);
    &::before {
      content: '';
      position: absolute;
      top: 50%;
      left: 0;
      transform: translateY(-45%);
      width: 1px;
      height: 12px;
      background: var(--gray-5);
    }
    &:first-child::before {
      display: none;
    }
    &:hover {
      color: var(--black);
    }
    &.on {
      color: var(--black);
    }
  }
`;

const Util = styled(Flex)`
  li {
    margin-left: 5px;
    a {
      display: flex;
      align-items: center;
      height: 45px;
      padding: 0 1.5rem;
      border-radius: 45px;
      background: var(--gray-2);
      white-space: nowrap;
      &:hover {
        color: #ccc;
      }
    }
  }
`;

const HeaderWrapper = styled.header`
  height: var(--header-height);
  background: var(--primary);
  border-bottom: 1px solid var(--gray-2);
  padding: 0 2%;
  h1 a {
    display: flex;
    align-items: center;
    width: 154px;
    height: 100%;
    margin-right: 1rem;
    background: url('/images/comm/logo.svg') no-repeat 50% 50%;
    text-indent: -9999px;
  }
`;

export default Header;
