import { defineStyle, defineStyleConfig } from '@chakra-ui/react';

const baseStyle = {
  maxW: '2560px',
  margin: '0 auto',
  px: '18px',
};

// define custom sizes
const sizes = {
  sm: defineStyle({
    maxW: '45ch',
    p: '4',
  }),
  md: defineStyle({
    maxW: 'container.sm',
    p: '6',
    fontSize: 'lg',
  }),
  lg: defineStyle({
    maxW: '1440px',
    p: '8',
    fontSize: 'xl',
  }),
};

// export the component theme
export const containerTheme = defineStyleConfig({ sizes });
