import { extendTheme, Container } from '@chakra-ui/react';
import { defineStyle, defineStyleConfig } from '@chakra-ui/styled-system';
import { createBreakpoints } from '@chakra-ui/theme-tools';

export const theme = extendTheme({
  Container: {
    baseStyle: {
      w: '100%',
      mx: 'auto',
      maxW: 'prose',
      px: '4',
    },
  },
});
