import { Container, FormControl, FormLabel, Heading, Input, Flex } from '@chakra-ui/react';
import { Form, Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';

const Header = () => {
  return (
    <HeaderWrapper>
      <div className="inner">
        <Heading as="h1" size="lg">
          logo
        </Heading>
        <Gnb>
          <ul>
            <li>
              <Link to="/Exchange">거래소</Link>
            </li>
            <li>
              <Link to="/Deposit">입출금</Link>
            </li>
            <li>
              <Link to="/Investment">투자내역</Link>
            </li>
            <li>
              <Link to="/Help">고객센터</Link>
            </li>
          </ul>
        </Gnb>
        <Flex>
          <FormControl>
            <FormLabel>Search</FormLabel>
            <Input type="text" />
          </FormControl>
          <ul className="util">
            <li>
              <Link to="/Login">로그인</Link>
            </li>
            <li>
              <Link to="/Signup">회원가입</Link>
            </li>
          </ul>
          <div className="lang">
            <Link to="/">KOR</Link>
            <Link to="/">ENG</Link>
          </div>
        </Flex>
      </div>
    </HeaderWrapper>
  );
};

const Tnb = styled.nav`
  display: flex;
  margin-left: auto;
`;

const HeaderWrapper = Flex`
  background: var(--white);
  border-bottom: 1px solid var(--gray-2);
  .inner {
    display: flex;
    align-items: center;
    height: 80px;
    padding: 0 5rem;
  }
  h1 {
    display: flex;
    align-items: center;
    height: 100%;
    margin-right: 1rem;
  }
`;

const Gnb = styled.nav`
  ul {
    display: flex;
    margin-left: auto;
    li {
      margin-left: 1rem;
      a {
        &:hover {
          color: #ccc;
        }
      }
    }
  }
`;

export default Header;
